#!/usr/bin/env python3
from codon_manipulator import SequenceAnalyzer

def test():
    from Bio.Seq import Seq
    from Bio.Alphabet import IUPAC

    sa = SequenceAnalyzer()
    seq = Seq('', IUPAC.unambiguous_dna)

    m2stop_count = sa.count_muts_to_stop(seq)
    print(m2stop_count)
    CpG_count, UpA_count = sa.count_CpG(seq)
    print(CpG_count, UpA_count)

    
# when run as its own script, 
if __name__ == "__main__":
    test()
